import React from "react";

import './navbar.css';

const Navbar = () => {

    const points = ['Опасность остеопороза',
        'Группа и факторы риска',
        'Симптомы и проявления',
        'Типы остеопороза',
        'Диагностика',
        'Профилактика и лечение',
        'Препараты',
        'Побочные эффекты и их устранение',
        'Золедронаты',
        'Дозировка',
        'Эффективность',
        'Длительность лечения',
        'Удобство использования'];



    const links = points.map((point, index) => {
        const clickHandler = (index) => {
            console.log(index)
            document.getElementById(index).classList.add('navbar__list_active')
        };
        return (
            <li key={index}
                className='navbar__li'
                id={index}
                onClick={() => clickHandler(index)}>
                <span className='navbar__number'>{index+1}.</span>
                <a href={'#card-' + (index + 1)}> {point}</a>
            </li>
        )
    });

    return (
        <div className='navbar'>
            <p className='navbar__title'>Содержание статьи:</p>
            <ul className='navbar__list'>
                {links}
            </ul>
        </div>
    )
};

export default Navbar;
