import React, { useState } from "react";
import AnchorLink from 'react-anchor-link-smooth-scroll';

import './navbar.css';

const Navbar = () => {

    const points = ['Опасность остеопороза',
        'Группа и факторы риска',
        'Симптомы и проявления',
        'Типы остеопороза',
        'Диагностика',
        'Профилактика и лечение',
        'Препараты',
        'Побочные эффекты и их устранение',
        'Золедронаты',
        'Дозировка',
        'Эффективность',
        'Длительность лечения',
        'Удобство использования'];

    const instructionPoints = ['Состав',
        'Фармакологическое действие',
        'Показания к применению',
        'Противопоказания',
        'Способ применения и дозы',
        'Инструкция по приготовлению раствора',
        'Побочное действие',
        'Передозировка',
        'Взаимодействие с другими лекарственными средствами',
        'Особые указания',
        'Влияние на способность управлять транспортными средствами и другими механизмами',
        'Форма выпуска',
        'Условия хранения',
        'Срок годности',
        'Условия отпуска'];


       const [ visible, setVisible ] = useState({
            pointsVisible: true,
            instructionPointsVisible: false
        });

        const path = window.location.pathname;

       const changeVisible = (path) => {

           if (path.includes('instruction')) {
               console.log('instruction')
               setVisible((visible) => {
                   return {
                       instructionPointsVisible: true
                   }
               })
           }
       };


    const [activeId, setState] = useState(0);

    const onActiveLink = (event, index) => {

        document.getElementById(index).childNodes.forEach(child =>
            child.classList.add('anchor_active')
        );
        if (index !== activeId){
            document.getElementById(activeId).childNodes.forEach(child => {
                child.classList.remove('anchor_active')
            })
        }
        setState(index);
    };

    const links = points.map((point, index) => {
        return (
            <li key={index}
                id={index}>
                <span className='navbar__number'>{index+1}. </span>
                <AnchorLink
                    offset={() => 80}
                    className='anchor'
                    href={'#card-' + (index + 1)}

                    onClick={(event) => onActiveLink(event, index)}
                >
                    {point}
                </AnchorLink>
            </li>
        )
    });

    return (
        <div className='navbar'>
            <p className='navbar__title'>Содержание статьи:</p>
            <ul className='navbar__list'>
                {links}
            </ul>
        </div>
    )
};

export default Navbar;
