import React, { useState } from "react";
import AnchorLink from 'react-anchor-link-smooth-scroll';

import './navbar.css';

const Navbar = () => {

    const points = ['Опасность остеопороза',
        'Группа и факторы риска',
        'Симптомы и проявления',
        'Типы остеопороза',
        'Диагностика',
        'Профилактика и лечение',
        'Препараты',
        'Побочные эффекты и их устранение',
        'Золедронаты',
        'Дозировка',
        'Эффективность',
        'Длительность лечения',
        'Удобство использования'];

    const [active, setState] = useState('false');

    const onActiveLink = () => {
        setState('true')
        const link = document.querySelector('.anchor');
        if ('true') {
            link.classList.add('anchor_active')
        } else {
            link.classList.remove('anchor_active')
        }
    };


    const links = points.map((point, index) => {
        return (
            <li key={index}>
                <span className='navbar__number'>{index+1}. </span>
                <AnchorLink
                    offset={() => 80}
                    className='anchor'
                    href={'#card-' + (index + 1)}
                    onClick={() => onActiveLink}
                >
                    {point}
                </AnchorLink>
            </li>
        )
    });

    return (
        <div className='navbar'>
            <p className='navbar__title'>Содержание статьи:</p>
            <ul className='navbar__list'>
                {links}
            </ul>
        </div>
    )
};

export default Navbar;
