import React, { useState, useEffect } from "react";
import AnchorLink from 'react-anchor-link-smooth-scroll';

import './navbar.css';

const Navbar = ({menu}) => {

    const [activeId, setState] = useState(0);

    const [currentSection, setCurrentSection] = useState({
        x: 0,
        y: 0
    });

    useEffect(() => {
        // const firstCard = document.querySelector('.main-content');
        const appContent = document.querySelector('.app__content');
        console.log('app_c', window.getComputedStyle(appContent))
        // console.log('was here', firstCard.getBoundingClientRect())


        currentSection.y = parseInt(window.getComputedStyle(appContent).top) + parseInt(window.getComputedStyle(appContent).marginTop);
        currentSection.x = appContent.lastChild.getBoundingClientRect().x;

        // console.log('was here', appContent.lastChild.getBoundingClientRect().x )
    }, []);

    const handleScroll = () => {

        const current_element = document.elementFromPoint(currentSection.x, currentSection.y);
        console.log('curr_elem', current_element.id.substr(5))
        onActiveLink(current_element.id.substr(5))

    };

    window.addEventListener('scroll', handleScroll);


    const onActiveLink = (event, index) => {
        document.getElementById(index).childNodes.forEach(child =>
            child.classList.add('anchor_active')
        );
        if (index !== activeId){
            document.getElementById(activeId).childNodes.forEach(child => {
                child.classList.remove('anchor_active')
            })
        }
        setState(index);
    };

    const links = menu.map((point, index) => {
        return (
            <li key={index}
                id={index}>
                <span className='navbar__number'>{index+1}. </span>
                <AnchorLink
                    offset={() => 80}
                    className='anchor'
                    href={'#card-' + (index + 1)}

                    onClick={(event) => onActiveLink(event, index)}
                >
                    {point}
                </AnchorLink>
            </li>
        )
    });

    return (
        <div className='navbar'>
            <p className='navbar__title'>Содержание статьи:</p>
            <ul className='navbar__list'>
                {links}
            </ul>
        </div>
    )
};

export default Navbar;
