import React, { useState } from "react";
import AnchorLink from 'react-anchor-link-smooth-scroll';

import './navbar.css';

const Navbar = () => {

    const points = ['Опасность остеопороза',
        'Группа и факторы риска',
        'Симптомы и проявления',
        'Типы остеопороза',
        'Диагностика',
        'Профилактика и лечение',
        'Препараты',
        'Побочные эффекты и их устранение',
        'Золедронаты',
        'Дозировка',
        'Эффективность',
        'Длительность лечения',
        'Удобство использования'];

    const [activeId, setState] = useState(0);

    const onActiveLink = (event, index) => {

        console.log(activeId)

        console.log(document.getElementById(index), 'lala')
        document.getElementById(index).classList.add('anchor_active')

        // document.getElementById(activeId).classList.remove('anchor_active')

        setState(index);

        // setState('true')
        // const link = document.querySelector('.anchor');
        // if ('true') {
        //     link.classList.add('anchor_active')
        // } else {
        //     link.classList.remove('anchor_active')
        // }
    };


    const links = points.map((point, index) => {
        return (
            <li key={index}>
                <span className='navbar__number'>{index+1}. </span>
                <AnchorLink
                    offset={() => 80}
                    className='anchor'
                    href={'#card-' + (index + 1)}
                    id={index}
                    onClick={(event) => onActiveLink(event, index)}
                >
                    {point}
                </AnchorLink>
            </li>
        )
    });

    return (
        <div className='navbar'>
            <p className='navbar__title'>Содержание статьи:</p>
            <ul className='navbar__list'>
                {links}
            </ul>
        </div>
    )
};

export default Navbar;
